import React,{useState} from 'react'
import {useParams } from 'react-router-dom'
import axios from 'axios';



import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

import ReactHtmlParser from "react-html-parser";


function createData(
  name: string,
  calories: number,
) 
{
  return { name, calories };
}

const rows = [
  createData('Price to RS', "₹ 19K"),
  createData('Rank', 1),
  createData('24h Volume', "₹ 64.3B"),
  createData('Market Cap',"₹ 364B"),
  createData('All-time-high(daily avg.)',"₹ 68.8K")
];

const Crypto = () => {

  const [dat, setDat] = useState({})
  const [htm, setHtm] = useState({})
  const x = useParams().id;
  axios.get(`https://coinranking1.p.rapidapi.com/coin/${x}`,{
    params: {referenceCurrencyUuid: `${x}`, timePeriod: '24h'},
    headers: {
    'X-RapidAPI-Key': '7cf9598f86mshbf1d594046b0bbap174be7jsn287c463a1d1f',
    'X-RapidAPI-Host': 'coinranking1.p.rapidapi.com'
  }
  })
  .then((response)=>{
    setDat(response.data.data.coin)
  })
  .catch((error)=>{console.log('error', error)})

  // console.log('dat.data.data.oin.', dat.data.data.coin.description)

  // console.log('data', data)
  // const desc = data.data.coin.description;

   setHtm(dat.description)
   console.log('htm', htm)
    // console.log('html', dat)
  return (

    <div>
    {
      useParams().id &&
      <div>
        <div className="mainNavBar">
          <div className="subPart">
            <div className="mainPage">
              <div className='cur'>
                <h2>Bitcoin (BTC) Price</h2>
                <p>Bitcoin live price in Indian Rupees (RS). View value statistics, market cap and supply.</p>
              </div>
              <div className='cur-table'>
                  <div className='cur-table1'>
                    <h3>Bitcoin value statistics</h3>
                    <p>An overview showing the statistics of Bitcoin, such as the base and quote currency, the rank, and trading volume.</p>
                    <TableContainer component={Paper}>
                    <Table sx={{ minWidth: 200 }} aria-label="simple table">
                      <TableBody>
                        {
                        rows.map((row) => (
                          <TableRow
                            key={row.name}
                            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                          >
                            <TableCell component="th" scope="row">
                              {row.name}
                            </TableCell>
                            <TableCell align="right">{row.calories}</TableCell>
                            <TableCell align="right" style={{fontWeight:"bold"}}>{row.fat}</TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                  </div>
                  <div className='cur-table2'>
                    <h3>Other Stats Info</h3>
                    <p>An overview showing the statistics of Bitcoin, such as the base and quote currency, the rank, and trading volume.</p>
                    <TableContainer component={Paper}>
                    <Table sx={{ minWidth: 200 }} aria-label="simple table">
                      <TableBody>
                        {rows.map((row) => (
                          <TableRow
                            key={row.name}
                            sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                          >
                            <TableCell component="th" scope="row">
                              {row.name}
                            </TableCell>
                            <TableCell align="right">{row.calories}</TableCell>
                            <TableCell align="right">{row.fat}</TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                  </div>
              </div>
                  <div>

                      {/* {ReactHtmlParser(html)} */}


                  </div>
            </div>
          </div>
          </div>
      </div>
    }
    </div>
  )
}

export default Crypto
