import "./App.css";
import axios from "axios";

const Full = () => {
  const [coin, setCoin] = useState([]);
  const [search, setSearch] = useState("")
  const [tempSearch, setTempSearch] = useState([])

  const navigate = useNavigate();
  useEffect(() => {
    axios
      .get("https://coinranking1.p.rapidapi.com/coins", {
        params: {
          referenceCurrencyUuid: "yhjMzLPhuIDl",
          timePeriod: "24h",
          orderBy: "marketCap",
          orderDirection: "desc",
          limit: "100",
          offset: "0",
        },
        headers: {
          "X-RapidAPI-Key":
            "d00cef50edmshebd666b51e7e5e2p144803jsnebecc2a2481c",
          "X-RapidAPI-Host": "coinranking1.p.rapidapi.com",
        },
      })
      .then((response) => {
        setCoin(response.data.data.coins);
      })
      .catch((error) => {
        console.log("error", error);
      });

      console.log('response', coin)
  }, []);

  axios.get("https://coinranking1.p.rapidapi.com/search-suggestions", {
    params: { referenceCurrencyUuid: "yhjMzLPhuIDl", query: `${search}`,count:6, freshness:"Day", textFormat:"Raw", safeSearch:"Off" },
    headers: {
      "X-RapidAPI-Key":
        "d00cef50edmshebd666b51e7e5e2p144803jsnebecc2a2481c",
      "X-RapidAPI-Host": "coinranking1.p.rapidapi.com",
