    headers: {
      'X-BingApis-SDK': 'true',
      'X-RapidAPI-Key': 'd00cef50edmshebd666b51e7e5e2p144803jsnebecc2a2481c',
      'X-RapidAPI-Host': 'bing-news-search1.p.rapidapi.com'
