                  }

                {

                tempSearch && 

                coin.map((item) => (
                  <div className="box" key={item.uuid} onClick={()=>{
                      navigate(`/CryptoCurrency/${item.uuid}`)
                  }}>
                    <div className="top-box">
                      <h3>{item.name}</h3>
                      <img
                        src={item.iconUrl}
                        height="30px"
                        width={"30px"}
                        alt="Crypto Currency"
                        style={{ marginTop: "10px" }}
                      />
                    </div>
                    <div className="bottom-box">
                      <p>
                        <i>Price</i> : {item.price}
                      </p>
                      <p>
                        <i>Market Cap </i>: {item.marketCap}
                      </p>
                      <p>
                        <i>Daily Change</i> : {item.change}
                      </p>
                    </div>
                  </div>
              ))}

            </div>
          </div>
        </div>
      </div>
}
    </div>
  );
};

export default Full;
