import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

const Full = () => {
  const [coin, setCoin] = useState([]);
  const [search, setSearch] = useState("")
  const [tempSearch, setTempSearch] = useState([])
  const [tr, setTr] = useState(false)
  useEffect(() => {
    axios
      .get("https://coinranking1.p.rapidapi.com/coins", {
        params: {
          referenceCurrencyUuid: "yhjMzLPhuIDl",
          timePeriod: "24h",
          orderBy: "marketCap",
          orderDirection: "desc",
          limit: "100",
          offset: "0",
        },
        headers: {
          "X-RapidAPI-Key":
            "d00cef50edmshebd666b51e7e5e2p144803jsnebecc2a2481c",
          "X-RapidAPI-Host": "coinranking1.p.rapidapi.com",
        },
      })
      .then((response) => {
        setCoin(response.data.data.coins);
      })
      .catch((error) => {
        console.log("error", error);
      });
  }, []);

  axios.get("https://coinranking1.p.rapidapi.com/search-suggestions", {
    params: { referenceCurrencyUuid: "yhjMzLPhuIDl", query: `${search}` },
    headers: {
      "X-RapidAPI-Key":
        "d00cef50edmshebd666b51e7e5e2p144803jsnebecc2a2481c",
      "X-RapidAPI-Host": "coinranking1.p.rapidapi.com",
    },
  })
  .then((response)=>{setTempSearch(response.data.data.coins) ; setTr(true)})
  .catch((error)=>{console.log('error', error)})

  if(search == "")
  {
    setTr(false);
  }

  console.log('tr', tr)
  return (
    <div>
      <div className="mainNavBar">
        <div className="subPart">
          <div className="mainPage">
            <div className="inp">
              <input
                type="text"
                placeholder="Search Here"
                className="mainInp"
                onChange={(e)=>{
                    setSearch(e.target.value);
                }}
              />
            </div>
            <div className="dabba">
              {
                // tempSearch &&

                //   tempSearch.map((item) => (
                //     <div className="box" key={item.id}>
                //       <div className="top-box">
                //         <h3>{item.name}</h3>
                //         <img
                //           src={item.iconUrl}
                //           height="30px"
                //           width={"30px"}
                //           alt="Crypto Currency"
                //           style={{ marginTop: "10px" }}
                //         />
                //       </div>
                //       <div className="bottom-box">
                //         <p>
                //           <i>Price</i> : {item.price}
                //         </p>
                //         <p>
                //           <i>Market Cap </i>: {item.marketCap}
                //         </p>
                //         <p>
                //           <i>Daily Change</i> : {item.change}
                //         </p>
                //       </div>
                //     </div>
                //   ))
                  }

                {

                tempSearch && 

                coin.map((item) => (
                  <div className="box" key={item.id}>
                    <div className="top-box">
                      <h3>{item.name}</h3>
                      <img
                        src={item.iconUrl}
                        height="30px"
                        width={"30px"}
                        alt="Crypto Currency"
                        style={{ marginTop: "10px" }}
                      />
                    </div>
                    <div className="bottom-box">
                      <p>
                        <i>Price</i> : {item.price}
                      </p>
                      <p>
                        <i>Market Cap </i>: {item.marketCap}
                      </p>
                      <p>
                        <i>Daily Change</i> : {item.change}
                      </p>
                    </div>
                  </div>
              ))}

            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Full;
